public class SquareRoot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x=17;
int j;
int i;
for (i=1;i<=x;i++)
{
{
	j=i*i;
}
	if (j==x)
	{
		System.out.println(i);
		break;
	}
	else
	{ 
		int k;
		k=i+1;
		if (x>i*i && x<k*k)
System.out.println (i);	}
	
}
		

	}

}
